package day4;

public class SwitchExample {

	public static void main(String[] args) {
		int Year = 2022;
		switch(Year) {
		case 2022:
		{
			System.out.println("it is a year");
			break;
		}
		case 2000:
		System.out.println("it is a year");
		break;
		default:
			System.out.println("invalid input");
			break;
		}
		}
		// TODO Auto-generated method stub

	}


